    const titleInput = document.querySelector("#titleInput");
    fetch("http://localhost:8000/todo", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: titleInput.value,
      }),
    })
      .then((res) => {
