        titleInput.value = "";
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <Form.Group
      className="mb-3 d-flex justify-content-between"
      controlId="formBasicEmail"
    >
      <Form.Control
        id="titleInput"
        type="text"
        placeholder={props.placeholder}
      />
      <Button
        variant="primary"
        size="lg"
        onClick={() => {
          addTodoItem();
        }}
      >
        +
      </Button>
    </Form.Group>
  );
};

export default TodoForm;
